INSERT INTO `Meeting_Flow`(`id`, `meeting_id`, `flow_name`, `flow_step`, `flow_content`, `status`, `date_time`) VALUES (3573, 100000077, NULL, 7, '{\"desc\":\"<p>投票说明</p><p>适用于上级机关答复下级机关的请示事项。复函，适用于不相隶属机关之间商洽工作，询问和答复问题，请求批准和答复审批事项。国务院《国家行政机关公文处理办法》国发〔2000〕23号第九条 行政机关的公文种类主要有：（一）命令（令）适用于依照有关法律公布行政法规和规章；宣布施行重大强制性行政措施；嘉奖有关单位及人员。（二）决定适用于对重要事项或者重大行动做出安排，奖惩有关单位及人员，变更或者撤销下级机关不适当的决定事项。（三） 公告适用于向国内外宣布重要事项或者法定事项。</p>\"}', 3, NULL);
