INSERT INTO `Meeting_Flow`(`id`, `meeting_id`, `flow_name`, `flow_step`, `flow_content`, `status`, `date_time`) VALUES (3703, 100000088, NULL, 5, '{\"desc\":\"<p> 选举办法</p><p><span style=\\\"color: rgb(51, 51, 51);\\\">世人多称“李中堂”，</span><a href=\\\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=12461&amp;ss_c=ssc.citiao.link\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(51, 102, 204);\\\">安徽省合肥市</a><span style=\\\"color: rgb(51, 51, 51);\\\">东乡（今瑶海区）磨店乡人，晚清名臣，洋务运动的主要领导人之一，与</span><a href=\\\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=553788&amp;ss_c=ssc.citiao.link\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(51, 102, 204);\\\">曾国藩</a><span style=\\\"color: rgb(51, 51, 51);\\\">、</span><a href=\\\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=129989&amp;ss_c=ssc.citiao.link\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(51, 102, 204);\\\">张之洞</a><span style=\\\"color: rgb(51, 51, 51);\\\">、</span><a href=\\\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=130057&amp;ss_c=ssc.citiao.link\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(51, 102, 204);\\\">左宗棠</a><span style=\\\"color: rgb(51, 51, 51);\\\">并称为“中兴四大名臣”。李鸿章官至</span><a href=\\\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=549816&amp;ss_c=ssc.citiao.link\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(51, 102, 204);\\\">东宫三师</a><span style=\\\"color: rgb(51, 51, 51);\\\">、</span><a href=\\\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=208587&amp;ss_c=ssc.citiao.link\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(51, 102, 204);\\\">文华殿大学士</a><span style=\\\"color: rgb(51, 51, 51);\\\">、</span><a href=\\\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=180322&amp;ss_c=ssc.citiao.link\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(51, 102, 204);\\\">北洋通商大臣</a><span style=\\\"color: rgb(51, 51, 51);\\\">、直隶总督，爵位一等肃毅伯。历经讨伐</span><a href=\\\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=5588032&amp;ss_c=ssc.citiao.link\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(51, 102, 204);\\\">太平军</a><span style=\\\"color: rgb(51, 51, 51);\\\">、平定捻军、洋务运动、中法战争、甲午战争、义和团运动，是清朝地方武装淮军的创建者和领导者，并且为清朝建立了一支西式海军北洋水师。</span></p>\",\"min\":\"57\"}', 1, NULL);
