INSERT INTO `Meeting_Flow`(`id`, `meeting_id`, `flow_name`, `flow_step`, `flow_content`, `status`, `date_time`) VALUES (3931, 100000107, NULL, 5, '{\"desc\":\"<p>\\t1989年2月17日出生于<a href=\\\"https://baike.baidu.com/item/%E5%A4%A9%E6%B4%A5%E5%B8%82/213824\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(19, 110, 194);\\\">天津市</a>，是一名中国<a href=\\\"https://baike.baidu.com/item/%E8%B6%B3%E7%90%83%E8%BF%90%E5%8A%A8%E5%91%98/20426278\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(19, 110, 194);\\\">足球运动员</a>。现效力于<a href=\\\"https://baike.baidu.com/item/%E5%8C%97%E4%BA%AC%E4%B8%AD%E8%B5%AB%E5%9B%BD%E5%AE%89%E8%B6%B3%E7%90%83%E4%BF%B1%E4%B9%90%E9%83%A8/20322752\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(19, 110, 194);\\\">北京中赫国安足球俱乐部</a>。<span style=\\\"color: rgb(51, 102, 204);\\\">&nbsp;[1-2]</span><span style=\\\"color: rgb(19, 110, 194);\\\">&nbsp;</span></p><p>\\t出身于天津火车头青训的王刚，先后效力于<a href=\\\"https://baike.baidu.com/item/%E6%9C%AC%E8%8F%B2%E5%8D%A1/3147071\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(19, 110, 194);\\\">本菲卡</a>、<a href=\\\"https://baike.baidu.com/item/%E8%B4%9D%E6%8B%89%E9%A9%AC%E5%B0%94/6874339\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(19, 110, 194);\\\">贝拉马尔</a>、科维良体育、<a href=\\\"https://baike.baidu.com/item/%E5%B1%B1%E4%B8%9C%E9%B2%81%E8%83%BD/144395\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(19, 110, 194);\\\">山东鲁能</a>、萨卡文人、北京人和等俱乐部<span style=\\\"color: rgb(51, 102, 204);\\\">&nbsp;[2]</span><span style=\\\"color: rgb(19, 110, 194);\\\">&nbsp;</span>&nbsp;。同时，王刚曾入选过国少、国青、国奥、国家队，并于2004年随国少队拿下亚少赛冠军。<span style=\\\"color: rgb(51, 102, 204);\\\">&nbsp;[1]</span><span style=\\\"color: rgb(19, 110, 194);\\\">&nbsp;</span></p><p>\\t2010年，王刚首次入选<a href=\\\"https://baike.baidu.com/item/%E4%B8%AD%E5%9B%BD%E5%9B%BD%E5%AE%B6%E7%94%B7%E5%AD%90%E8%B6%B3%E7%90%83%E9%98%9F/5152172\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(19, 110, 194);\\\">中国国家男子足球队</a>。2019年，王刚再次入选中国国家男子足球队。<span style=\\\"color: rgb(51, 102, 204);\\\">&nbsp;[3]</span><span style=\\\"color: rgb(19, 110, 194);\\\">&nbsp;</span>&nbsp;2020年1月，王刚入选<a href=\\\"https://baike.baidu.com/item/%E8%81%94%E8%B5%9B%E4%BC%98%E7%A7%80%E4%B8%AD%E5%9B%BD%E7%B1%8D%E7%90%83%E5%91%98%E6%8A%80%E6%9C%AF%E6%A1%A3%E6%A1%88%E5%BA%93/24248902\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(19, 110, 194);\\\">联赛优秀中国籍球员技术档案库</a>。</p>\",\"min\":\"9\"}', 1, NULL);
