INSERT INTO `Meeting_Flow`(`id`, `meeting_id`, `flow_name`, `flow_step`, `flow_content`, `status`, `date_time`) VALUES (4003, 100000113, NULL, 5, '{\"desc\":\"<p>选举办法：</p><p><span style=\\\"color: rgb(51, 51, 51);\\\">   1992年小敏姊妹在聚会中被捕，但她在拘留所中唱诗歌，放胆传扬</span>\\t<a href=\\\"https://baike.baidu.com/item/%E7%A6%8F%E9%9F%B3/84881\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(19, 110, 194);\\\">福音</a>\\t<span style=\\\"color: rgb(51, 51, 51);\\\">。在拘留所里，什么样的犯人都有，可是在那里面，她不仅自己唱，还教同牢房的几个女犯一块儿唱赞美神的歌。司法干警就说：“你们怎么啦？人家进来都是千方百计地想办法出去，你可好，倒住下了，还高兴地唱歌？”当然她坐的时间不长，她坐了不到半个月就放出来了。</span></p>\",\"min\":\"9\"}', 3, NULL);
